mapper.select:
select count(*) from banxing_order o
where o.tutor_id = #{tutorId, jdbcType = VARCHAR}

    and o.order_status = #{orderStatus, jdbcType = INTEGER}


    and o.order_status > #{orderStatus, jdbcType = INTEGER}

select o.id as orderCode, o.reserve_date as reserveDate, o.reserve_start_time as reserveStartTime, o.reserve_end_time as reserveEndTime, u.nick_name as userName from banxing_order o left join banxing_user u on o.user_id = u.id where o.tutor_id = #{tutorId, jdbcType = VARCHAR}
and o.order_status = #{orderStatus, jdbcType = INTEGER}
and o.order_status > #{orderStatus, jdbcType = INTEGER}
order by o.create_time asc, o.id asc limit #{offset}, #{pageSize}
